import ajax from 'axios';

const API_KEY = '19834827-b752d1c95cba28ff11bc03641';
const BASE_URL = 'https://pixabay.com/api';
const imageRequest = ({ page = 1, query = '', perPage = 12 }) => {
  return fetch(
    `${BASE_URL}/?q=${query}&page=${page}&key=${API_KEY}&image_type=photo&orientation=horizontal&per_page=${perPage}`,
  )
    .then(data => console.log(data.json()))
    .then(response => response.data.hits);
};

export default imageRequest;
